model.schema:
{
"groups":[
{
"legend":"Text Inputs",
"fields": [
  {
    "type": "label",
    "label": "Label",
    "model": "label"
  },{
    "type": "input",
    "inputType": "text",
    "label": "Text",
    "model": "text",
    "id": "text",
    "placeholder": "Text"
  },{
    "type": "textArea",
    "label": "Text Area",
    "model": "textarea",
    "id": "textarea",
    "rows": 4,
    "placeholder": "Enter text here"
  }
  ]
},
{
"legend":"Other Inputs",
"fields": [
  {
    "type": "radios",
    "label": "Radios",
    "model": "radios",
    "values": [
      "Option 1",
      "Option 2"
    ]
  },{
    "type": "checkbox",
    "label": "Checkbox",
    "model": "checkbox",
    "default": true
  },{
    "type": "checklist",
    "label": "Checklist",
    "model": "checklist",
    "listBox": true,
    "values": [
      "Option 1",
      "Option 2"
    ]
  },{
    "type": "select",
    "label": "Select",
    "model": "select",
    "values": [
      "Option 1",
      "Option 2"
    ]
  },{
    "type": "submit",
    "buttonText": "Submit Button",
    "validateBeforeSubmit": false
  }
]}]}
